import './styles.css';

import React, { useCallback, useEffect, useState } from 'react'
import { closeCart, openCart } from '../../redux/cart/cartSlice';
import { useDispatch, useSelector } from "react-redux";

import { ReactComponent as CartIcon } from '../../assets/svg/cart.svg';
import DropdownHead from '../dropdown/dropdown';
import DropdownItem from '../dropdown/dropdownItem';
import { LOAD_CURRENCIES } from '../../services/queries';
// import { LOAD_CURRENCIES } from '../services/queries';
import { ReactComponent as LogoIcon } from '../../assets/svg/logo.svg'
import { useQuery } from '@apollo/client'

function Nav() {
    const dispatch = useDispatch()
    const isCartOpen = useSelector((state) => state.cart.isCartOpen);
    const { error, loading, data } = useQuery(LOAD_CURRENCIES)
    // const [isCartOpen, setIsCartOpen] = useState(true);
    const [currencyList, setCurrencyList] = useState([]);
    const [selectedCurrency, setSelectedCurrency] = useState({
        label: "USD",
        symbol: "$"
    });

    useEffect(() => {
        if (data && data.currencies) {
            setCurrencyList(data.currencies);
            console.log(data);
        }
    }, [data])

    const handleToggleCart = useCallback(
        () => {
            if (isCartOpen) {
                return dispatch(closeCart())
            }
            dispatch(openCart())
        },
        [dispatch, isCartOpen],
    )

    const handleSelectCurrency = useCallback(
        (_currency) => {
            setSelectedCurrency({
                label: _currency.label,
                symbol: _currency.symbol
            })
        },
        [],
    )
    console.log(currencyList)
    return (
        <div className={`nav__container`}>
            {/* <div className={`nav__container ${isCartOpen ? "--fullScreen" : ""}`}> */}
            <div className='nav__left'>
                <div className='nav__left--item --active'>
                    <span className='nav__left--text --active'>Women</span>
                </div>
                <div className='nav__left--item'>
                    <span className='nav__left--text'>Men</span>
                </div>
                <div className='nav__left--item'>
                    <span className='nav__left--text'>Kids</span>
                </div>
            </div>
            <div className='nav__center'>
                <LogoIcon />
            </div>
            <div className='nav__right'>
                <div className='dropdown'>
                    <DropdownHead
                        value={<span>{selectedCurrency && selectedCurrency.symbol}</span>}
                    >
                        {currencyList && currencyList.length > 0 && currencyList.map(_currency => (
                            <DropdownItem onClick={() => handleSelectCurrency(_currency)} >
                                {_currency.symbol}
                                <span className='nav__dropdown--item'>
                                    {_currency.label}
                                </span>
                            </DropdownItem>
                        ))}
                    </DropdownHead>
                </div>
                <div className='cart-icon badge' data-badge={2} onClick={handleToggleCart} >
                    <CartIcon />
                </div>
            </div>
            {/* {
                isCartOpen &&
                <Cart />
            } */}
        </div>
    )
}

export default Nav
